import React, { useState, useEffect } from 'react';
import {
  Container, Table, Button, Alert, Spinner,
  Modal, Form, Row, Col, Card, Badge
} from 'react-bootstrap';
import { atendenteService } from '../services/api';

const Atendentes = () => {
  const [atendentes, setAtendentes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingAtendente, setEditingAtendente] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    funcao: '',
    telefone: '',
    email: ''
  });

  // Op√ß√µes de fun√ß√£o para o select
  const funcoes = [
    'Recep√ß√£o',
    'Atendimento',
    'Apoio',
    'Administrativo',
    'Coordena√ß√£o'
  ];

  useEffect(() => {
    carregarAtendentes();
  }, []);

  const carregarAtendentes = async () => {
    try {
      setLoading(true);
      const response = await atendenteService.listar();
      setAtendentes(response.data);
      setError('');
    } catch (err) {
      setError('Erro ao carregar atendentes. Verifique se o servidor est√° rodando.');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingAtendente) {
        await atendenteService.atualizar(editingAtendente.id, formData);
      } else {
        await atendenteService.criar(formData);
      }
      setShowModal(false);
      setEditingAtendente(null);
      resetForm();
      carregarAtendentes();
    } catch (err) {
      setError(`Erro ao ${editingAtendente ? 'atualizar' : 'criar'} atendente`);
    }
  };

  const handleEdit = (atendente) => {
    setEditingAtendente(atendente);
    setFormData({
      nome: atendente.nome,
      funcao: atendente.funcao || '',
      telefone: atendente.telefone || '',
      email: atendente.email || ''
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este atendente?')) {
      try {
        await atendenteService.excluir(id);
        carregarAtendentes();
      } catch (err) {
        setError('Erro ao excluir atendente');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      funcao: '',
      telefone: '',
      email: ''
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingAtendente(null);
    resetForm();
  };

  const getFuncaoBadge = (funcao) => {
    const variants = {
      'Recep√ß√£o': 'primary',
      'Atendimento': 'success',
      'Apoio': 'info',
      'Administrativo': 'warning',
      'Coordena√ß√£o': 'danger'
    };
    return (
      <Badge bg={variants[funcao] || 'secondary'}>
        {funcao}
      </Badge>
    );
  };

  const contarPorFuncao = (funcao) => {
    return atendentes.filter(a => a.funcao === funcao).length;
  };

  if (loading) {
    return (
      <Container className="my-4">
        <div className="text-center">
          <Spinner animation="border" role="status" className="me-2" />
          Carregando atendentes...
        </div>
      </Container>
    );
  }

  return (
    <Container className="my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üë®‚Äçüíº Atendentes</h2>
        <Button variant="success" onClick={() => setShowModal(true)}>
          ‚ûï Novo Atendente
        </Button>
      </div>

      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}

      {/* Cards de Estat√≠sticas */}
      <Row className="mb-4">
        <Col md={2}>
          <Card className="text-center bg-primary text-white">
            <Card.Body>
              <Card.Title>{atendentes.length}</Card.Title>
              <Card.Text>Total</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        {funcoes.map(funcao => (
          <Col md={2} key={funcao}>
            <Card className="text-center">
              <Card.Body>
                <Card.Title>{contarPorFuncao(funcao)}</Card.Title>
                <Card.Text>
                  <small>{funcao}</small>
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {atendentes.length === 0 ? (
        <Alert variant="info">
          Nenhum atendente cadastrado. Clique em "Novo Atendente" para come√ßar.
        </Alert>
      ) : (
        <div className="table-responsive">
          <Table striped bordered hover>
            <thead className="table-dark">
              <tr>
                <th>Nome</th>
                <th>Fun√ß√£o</th>
                <th>Telefone</th>
                <th>E-mail</th>
                <th>Data Cadastro</th>
                <th>A√ß√µes</th>
              </tr>
            </thead>
            <tbody>
              {atendentes.map(atendente => (
                <tr key={atendente.id}>
                  <td>
                    <strong>{atendente.nome}</strong>
                  </td>
                  <td>{getFuncaoBadge(atendente.funcao)}</td>
                  <td>
                    {atendente.telefone ? (
                      <a href={`tel:${atendente.telefone}`}>{atendente.telefone}</a>
                    ) : '-'}
                  </td>
                  <td>
                    {atendente.email ? (
                      <a href={`mailto:${atendente.email}`}>{atendente.email}</a>
                    ) : '-'}
                  </td>
                  <td>
                    {new Date(atendente.createdAt).toLocaleDateString('pt-BR')}
                  </td>
                  <td>
                    <Button 
                      variant="outline-primary" 
                      size="sm" 
                      className="me-1"
                      onClick={() => handleEdit(atendente)}
                    >
                      ‚úèÔ∏è
                    </Button>
                    <Button 
                      variant="outline-danger" 
                      size="sm"
                      onClick={() => handleDelete(atendente.id)}
                    >
                      üóëÔ∏è
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )}

      {/* Modal Atendente */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            {editingAtendente ? 'Editar Atendente' : 'Novo Atendente'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row>
              <Col md={8}>
                <Form.Group className="mb-3">
                  <Form.Label>Nome Completo *</Form.Label>
                  <Form.Control
                    type="text"
                    name="nome"
                    value={formData.nome}
                    onChange={handleChange}
                    placeholder="Digite o nome completo"
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Fun√ß√£o *</Form.Label>
                  <Form.Select
                    name="funcao"
                    value={formData.funcao}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Selecione a fun√ß√£o</option>
                    {funcoes.map(funcao => (
                      <option key={funcao} value={funcao}>
                        {funcao}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Telefone</Form.Label>
                  <Form.Control
                    type="tel"
                    name="telefone"
                    value={formData.telefone}
                    onChange={handleChange}
                    placeholder="(11) 99999-9999"
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>E-mail</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="email@exemplo.com"
                  />
                </Form.Group>
              </Col>
            </Row>

            <div className="text-muted small">
              * Campos obrigat√≥rios
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Cancelar
            </Button>
            <Button variant="primary" type="submit">
              {editingAtendente ? 'Atualizar' : 'Cadastrar'} Atendente
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default Atendentes;
