import React, { useState, useEffect } from 'react';
import {
  Container, Table, Button, Alert, Spinner,
  Modal, Form, Row, Col, Card, Badge
} from 'react-bootstrap';
import { medicoService, especialidadeService } from '../services/api';

const Medicos = () => {
  const [medicos, setMedicos] = useState([]);
  const [especialidades, setEspecialidades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [showEspecialidadeModal, setShowEspecialidadeModal] = useState(false);
  const [editingMedico, setEditingMedico] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    crm: '',
    telefone: '',
    email: '',
    especialidade_id: ''
  });
  const [novaEspecialidade, setNovaEspecialidade] = useState('');

  useEffect(() => {
    carregarDados();
  }, []);

  const carregarDados = async () => {
    try {
      setLoading(true);
      const [medicosRes, especialidadesRes] = await Promise.all([
        medicoService.listar(),
        especialidadeService.listar()
      ]);
      
      setMedicos(medicosRes.data);
      setEspecialidades(especialidadesRes.data);
      setError('');
    } catch (err) {
      setError('Erro ao carregar dados. Verifique se o servidor est√° rodando.');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingMedico) {
        await medicoService.atualizar(editingMedico.id, formData);
      } else {
        await medicoService.criar(formData);
      }
      setShowModal(false);
      setEditingMedico(null);
      resetForm();
      carregarDados();
    } catch (err) {
      setError(`Erro ao ${editingMedico ? 'atualizar' : 'criar'} m√©dico`);
    }
  };

  const handleAddEspecialidade = async (e) => {
    e.preventDefault();
    try {
      await especialidadeService.criar({ nome: novaEspecialidade });
      setNovaEspecialidade('');
      setShowEspecialidadeModal(false);
      carregarDados();
    } catch (err) {
      setError('Erro ao criar especialidade');
    }
  };

  const handleEdit = (medico) => {
    setEditingMedico(medico);
    setFormData({
      nome: medico.nome,
      crm: medico.crm,
      telefone: medico.telefone || '',
      email: medico.email || '',
      especialidade_id: medico.especialidade_id || ''
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este m√©dico?')) {
      try {
        await medicoService.excluir(id);
        carregarDados();
      } catch (err) {
        setError('Erro ao excluir m√©dico');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      crm: '',
      telefone: '',
      email: '',
      especialidade_id: ''
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingMedico(null);
    resetForm();
  };

  if (loading) {
    return (
      <Container className="my-4">
        <div className="text-center">
          <Spinner animation="border" role="status" className="me-2" />
          Carregando m√©dicos...
        </div>
      </Container>
    );
  }

  return (
    <Container className="my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üë®‚Äç‚öïÔ∏è M√©dicos</h2>
        <div>
          <Button 
            variant="outline-secondary" 
            className="me-2"
            onClick={() => setShowEspecialidadeModal(true)}
          >
            ‚ûï Especialidade
          </Button>
          <Button variant="success" onClick={() => setShowModal(true)}>
            ‚ûï Novo M√©dico
          </Button>
        </div>
      </div>

      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}

      <Row className="mb-4">
        <Col md={3}>
          <Card className="text-center bg-primary text-white">
            <Card.Body>
              <Card.Title>{medicos.length}</Card.Title>
              <Card.Text>Total de M√©dicos</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center bg-success text-white">
            <Card.Body>
              <Card.Title>{especialidades.length}</Card.Title>
              <Card.Text>Especialidades</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center bg-info text-white">
            <Card.Body>
              <Card.Title>
                {[...new Set(medicos.map(m => m.especialidade_id))].length}
              </Card.Title>
              <Card.Text>Especialidades Ativas</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center bg-warning text-dark">
            <Card.Body>
              <Card.Title>
                {medicos.filter(m => m.email && m.email.trim() !== '').length}
              </Card.Title>
              <Card.Text>Com E-mail</Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {medicos.length === 0 ? (
        <Alert variant="info">
          Nenhum m√©dico cadastrado. Clique em "Novo M√©dico" para come√ßar.
        </Alert>
      ) : (
        <div className="table-responsive">
          <Table striped bordered hover>
            <thead className="table-dark">
              <tr>
                <th>Nome</th>
                <th>CRM</th>
                <th>Especialidade</th>
                <th>Telefone</th>
                <th>E-mail</th>
                <th>A√ß√µes</th>
              </tr>
            </thead>
            <tbody>
              {medicos.map(medico => (
                <tr key={medico.id}>
                  <td>
                    <strong>{medico.nome}</strong>
                  </td>
                  <td>
                    <Badge bg="secondary">{medico.crm}</Badge>
                  </td>
                  <td>
                    {medico.especialidade ? (
                      <Badge bg="success">{medico.especialidade.nome}</Badge>
                    ) : (
                      <Badge bg="warning">Sem especialidade</Badge>
                    )}
                  </td>
                  <td>{medico.telefone || '-'}</td>
                  <td>
                    {medico.email ? (
                      <small>
                        <a href={`mailto:${medico.email}`}>{medico.email}</a>
                      </small>
                    ) : '-'}
                  </td>
                  <td>
                    <Button 
                      variant="outline-primary" 
                      size="sm" 
                      className="me-1"
                      onClick={() => handleEdit(medico)}
                    >
                      ‚úèÔ∏è
                    </Button>
                    <Button 
                      variant="outline-danger" 
                      size="sm"
                      onClick={() => handleDelete(medico.id)}
                    >
                      üóëÔ∏è
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )}

      {/* Modal M√©dico */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            {editingMedico ? 'Editar M√©dico' : 'Novo M√©dico'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row>
              <Col md={8}>
                <Form.Group className="mb-3">
                  <Form.Label>Nome Completo *</Form.Label>
                  <Form.Control
                    type="text"
                    name="nome"
                    value={formData.nome}
                    onChange={handleChange}
                    placeholder="Digite o nome completo"
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>CRM *</Form.Label>
                  <Form.Control
                    type="text"
                    name="crm"
                    value={formData.crm}
                    onChange={handleChange}
                    placeholder="Ex: SP-123456"
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Telefone</Form.Label>
                  <Form.Control
                    type="tel"
                    name="telefone"
                    value={formData.telefone}
                    onChange={handleChange}
                    placeholder="(11) 99999-9999"
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>E-mail</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="email@exemplo.com"
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Especialidade *</Form.Label>
              <div className="d-flex gap-2">
                <Form.Select
                  name="especialidade_id"
                  value={formData.especialidade_id}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione uma especialidade</option>
                  {especialidades.map(esp => (
                    <option key={esp.id} value={esp.id}>
                      {esp.nome}
                    </option>
                  ))}
                </Form.Select>
                <Button 
                  variant="outline-secondary" 
                  onClick={() => setShowEspecialidadeModal(true)}
                >
                  +
                </Button>
              </div>
            </Form.Group>

            <div className="text-muted small">
              * Campos obrigat√≥rios
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Cancelar
            </Button>
            <Button variant="primary" type="submit">
              {editingMedico ? 'Atualizar' : 'Cadastrar'} M√©dico
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Modal Especialidade */}
      <Modal show={showEspecialidadeModal} onHide={() => setShowEspecialidadeModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Nova Especialidade</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleAddEspecialidade}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Nome da Especialidade</Form.Label>
              <Form.Control
                type="text"
                value={novaEspecialidade}
                onChange={(e) => setNovaEspecialidade(e.target.value)}
                placeholder="Ex: Cardiologia, Pediatria..."
                required
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowEspecialidadeModal(false)}>
              Cancelar
            </Button>
            <Button variant="primary" type="submit">
              Adicionar Especialidade
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default Medicos;
