import React, { useState, useEffect } from 'react';
import { 
  Container, Table, Button, Alert, Spinner, 
  Modal, Form, Row, Col, Card, Badge 
} from 'react-bootstrap';
import { consultaService, pacienteService, medicoService, atendenteService } from '../services/api';

const Consultas = () => {
  const [consultas, setConsultas] = useState([]);
  const [pacientes, setPacientes] = useState([]);
  const [medicos, setMedicos] = useState([]);
  const [atendentes, setAtendentes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [showDetalhesModal, setShowDetalhesModal] = useState(false);
  const [consultaSelecionada, setConsultaSelecionada] = useState(null);
  const [formData, setFormData] = useState({
    data_consulta: '',
    hora_consulta: '',
    status: 'agendada',
    observacoes: '',
    valor_consulta: '',
    paciente_id: '',
    medico_id: '',
    atendente_id: ''
  });

  useEffect(() => {
    carregarDados();
  }, []);

  const carregarDados = async () => {
    try {
      setLoading(true);
      const [consultasRes, pacientesRes, medicosRes, atendentesRes] = await Promise.all([
        consultaService.listar(),
        pacienteService.listar(),
        medicoService.listar(),
        atendenteService.listar()
      ]);
      
      setConsultas(consultasRes.data);
      setPacientes(pacientesRes.data);
      setMedicos(medicosRes.data);
      setAtendentes(atendentesRes.data);
      setError('');
    } catch (err) {
      setError('Erro ao carregar dados. Verifique se o servidor está rodando.');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await consultaService.criar(formData);
      setShowModal(false);
      setFormData({
        data_consulta: '',
        hora_consulta: '',
        status: 'agendada',
        observacoes: '',
        valor_consulta: '',
        paciente_id: '',
        medico_id: '',
        atendente_id: ''
      });
      carregarDados();
    } catch (err) {
      setError('Erro ao criar consulta');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Função para visualizar detalhes da consulta
  const visualizarDetalhes = (consulta) => {
    setConsultaSelecionada(consulta);
    setShowDetalhesModal(true);
  };

  const formatarData = (dataString) => {
    return new Date(dataString).toLocaleDateString('pt-BR');
  };

  const formatarDataCompleta = (dataString) => {
    return new Date(dataString).toLocaleDateString('pt-BR', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatarHora = (horaString) => {
    return horaString?.substring(0, 5);
  };

  const getStatusBadge = (status) => {
    const variants = {
      agendada: 'warning',
      realizada: 'success',
      cancelada: 'danger'
    };
    return <Badge bg={variants[status]}>{status}</Badge>;
  };

  const getStatusTexto = (status) => {
    const textos = {
      agendada: 'Consulta agendada',
      realizada: 'Consulta realizada',
      cancelada: 'Consulta cancelada'
    };
    return textos[status] || status;
  };

  if (loading) {
    return (
      <Container className="my-4">
        <div className="text-center">
          <Spinner animation="border" role="status" className="me-2" />
          Carregando consultas...
        </div>
      </Container>
    );
  }

  return (
    <Container className="my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>📅 Consultas</h2>
        <Button variant="success" onClick={() => setShowModal(true)}>
          ➕ Nova Consulta
        </Button>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row className="mb-4">
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>{consultas.length}</Card.Title>
              <Card.Text>Total de Consultas</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>
                {consultas.filter(c => c.status === 'agendada').length}
              </Card.Title>
              <Card.Text>Agendadas</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>
                {consultas.filter(c => c.status === 'realizada').length}
              </Card.Title>
              <Card.Text>Realizadas</Card.Text>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>
                {consultas.filter(c => c.status === 'cancelada').length}
              </Card.Title>
              <Card.Text>Canceladas</Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {consultas.length === 0 ? (
        <Alert variant="info">
          Nenhuma consulta encontrada. Clique em "Nova Consulta" para começar.
        </Alert>
      ) : (
        <div className="table-responsive">
          <Table striped bordered hover>
            <thead className="table-dark">
              <tr>
                <th>Data</th>
                <th>Hora</th>
                <th>Paciente</th>
                <th>Médico</th>
                <th>Status</th>
                <th>Valor</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {consultas.map(consulta => (
                <tr key={consulta.id}>
                  <td>{formatarData(consulta.data_consulta)}</td>
                  <td>{formatarHora(consulta.hora_consulta)}</td>
                  <td>{consulta.paciente?.nome || 'N/A'}</td>
                  <td>{consulta.medico?.nome || 'N/A'}</td>
                  <td>{getStatusBadge(consulta.status)}</td>
                  <td>R$ {consulta.valor_consulta}</td>
                  <td>
                    <Button 
                      variant="outline-info" 
                      size="sm" 
                      className="me-1"
                      onClick={() => visualizarDetalhes(consulta)}
                      title="Ver detalhes"
                    >
                      👁️
                    </Button>
                    <Button variant="outline-danger" size="sm" title="Excluir">
                      🗑️
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )}

      {/* Modal Nova Consulta */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Nova Consulta</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Data da Consulta</Form.Label>
                  <Form.Control
                    type="date"
                    name="data_consulta"
                    value={formData.data_consulta}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Hora</Form.Label>
                  <Form.Control
                    type="time"
                    name="hora_consulta"
                    value={formData.hora_consulta}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Paciente</Form.Label>
                  <Form.Select
                    name="paciente_id"
                    value={formData.paciente_id}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Selecione um paciente</option>
                    {pacientes.map(paciente => (
                      <option key={paciente.id} value={paciente.id}>
                        {paciente.nome}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Médico</Form.Label>
                  <Form.Select
                    name="medico_id"
                    value={formData.medico_id}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Selecione um médico</option>
                    {medicos.map(medico => (
                      <option key={medico.id} value={medico.id}>
                        {medico.nome} - {medico.especialidade?.nome}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Atendente</Form.Label>
                  <Form.Select
                    name="atendente_id"
                    value={formData.atendente_id}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Selecione um atendente</option>
                    {atendentes.map(atendente => (
                      <option key={atendente.id} value={atendente.id}>
                        {atendente.nome}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Valor (R$)</Form.Label>
                  <Form.Control
                    type="number"
                    step="0.01"
                    name="valor_consulta"
                    value={formData.valor_consulta}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Status</Form.Label>
              <Form.Select
                name="status"
                value={formData.status}
                onChange={handleChange}
              >
                <option value="agendada">Agendada</option>
                <option value="realizada">Realizada</option>
                <option value="cancelada">Cancelada</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Observações</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="observacoes"
                value={formData.observacoes}
                onChange={handleChange}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Cancelar
            </Button>
            <Button variant="primary" type="submit">
              Agendar Consulta
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Modal Detalhes da Consulta */}
      <Modal show={showDetalhesModal} onHide={() => setShowDetalhesModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Detalhes da Consulta</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {consultaSelecionada && (
            <div>
              <Row className="mb-4">
                <Col md={8}>
                  <h5 className="text-primary">Informações da Consulta</h5>
                  <Table bordered>
                    <tbody>
                      <tr>
                        <td><strong>Data:</strong></td>
                        <td>{formatarDataCompleta(consultaSelecionada.data_consulta)}</td>
                      </tr>
                      <tr>
                        <td><strong>Hora:</strong></td>
                        <td>{formatarHora(consultaSelecionada.hora_consulta)}</td>
                      </tr>
                      <tr>
                        <td><strong>Status:</strong></td>
                        <td>{getStatusBadge(consultaSelecionada.status)}</td>
                      </tr>
                      <tr>
                        <td><strong>Valor:</strong></td>
                        <td>R$ {consultaSelecionada.valor_consulta}</td>
                      </tr>
                      <tr>
                        <td><strong>Observações:</strong></td>
                        <td>
                          {consultaSelecionada.observacoes || 
                            <span className="text-muted">Nenhuma observação</span>
                          }
                        </td>
                      </tr>
                    </tbody>
                  </Table>
                </Col>
                <Col md={4}>
                  <div className="text-center">
                    <div style={{ fontSize: '4rem' }}>📋</div>
                    <h6>Consulta #{consultaSelecionada.id}</h6>
                    <p className="text-muted">
                      {getStatusTexto(consultaSelecionada.status)}
                    </p>
                  </div>
                </Col>
              </Row>

              <hr />

              <Row>
                <Col md={4}>
                  <h6 className="text-success">👤 Paciente</h6>
                  <Card>
                    <Card.Body>
                      <strong>{consultaSelecionada.paciente?.nome || 'N/A'}</strong>
                      <br />
                      {consultaSelecionada.paciente?.telefone && (
                        <>📞 {consultaSelecionada.paciente.telefone}<br /></>
                      )}
                      {consultaSelecionada.paciente?.convenio && (
                        <>🏥 {consultaSelecionada.paciente.convenio}</>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={4}>
                  <h6 className="text-primary">👨‍⚕️ Médico</h6>
                  <Card>
                    <Card.Body>
                      <strong>{consultaSelecionada.medico?.nome || 'N/A'}</strong>
                      <br />
                      {consultaSelecionada.medico?.crm && (
                        <>🆔 {consultaSelecionada.medico.crm}<br /></>
                      )}
                      {consultaSelecionada.medico?.especialidade?.nome && (
                        <>🎯 {consultaSelecionada.medico.especialidade.nome}</>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={4}>
                  <h6 className="text-warning">👨‍💼 Atendente</h6>
                  <Card>
                    <Card.Body>
                      <strong>{consultaSelecionada.atendente?.nome || 'N/A'}</strong>
                      <br />
                      {consultaSelecionada.atendente?.funcao && (
                        <>💼 {consultaSelecionada.atendente.funcao}</>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {consultaSelecionada.pagamento && (
                <>
                  <hr />
                  <Row>
                    <Col>
                      <h6 className="text-success">💳 Pagamento</h6>
                      <Card>
                        <Card.Body>
                          <strong>Status:</strong> {consultaSelecionada.pagamento.status}
                          <br />
                          <strong>Forma:</strong> {consultaSelecionada.pagamento.forma_pagamento}
                          <br />
                          <strong>Valor Pago:</strong> R$ {consultaSelecionada.pagamento.valor_pago}
                          <br />
                          <strong>Data:</strong> {formatarData(consultaSelecionada.pagamento.data_pagamento)}
                        </Card.Body>
                      </Card>
                    </Col>
                  </Row>
                </>
              )}
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDetalhesModal(false)}>
            Fechar
          </Button>
          <Button variant="primary">
            Editar Consulta
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Consultas;
